import { Component } from '@angular/core';
import { FormGroup,FormBuilder,FormControl,Validators} from '@angular/forms';
import { AuthService } from 'src/app/shared/services/auth/auth.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styles: [
  ]
})

export class LoginComponent {
  isLogin=false;
  loginForm!:FormGroup;
  constructor(private formBuilder:FormBuilder, private authService:AuthService){
    this.loginForm=this.formBuilder.group({
      email:new FormControl('',[Validators.required,Validators.email]),
      password:new FormControl('',[Validators.required,Validators.min(6),Validators.max(16)])
    })

  }


  onSubmit(){
    const email=this.loginForm.value.email;
    const password=this.loginForm.value.password;
    if(email=='davidpaul@test.com'&&password=='david12345'){
      this.authService.login();
    } else alert('Invalid input');
    this.loginForm.reset();
  }
}
<div class="mt-10 p-6 max-w-md mx-auto bg-white shadow-xl rounded-xl border border-gray-100">
  <h4 class="py-3 text-gray-800 text-2xl font-semibold text-center border-b">Login</h4>
  <div class="mt-6">
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">

      <!-- Email Field -->
      <div class="mb-5">
        <label for="email" class="block mb-1 text-sm font-medium text-gray-700">Email address</label>
        <input 
          type="email"
          id="email"
          formControlName="email"
          placeholder="you@example.com"
          class="w-full p-3 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 outline-none"
          required>
      </div>

      <!-- Password Field -->
      <div class="mb-5">
        <label for="password" class="block mb-1 text-sm font-medium text-gray-700">Password</label>
        <input 
          type="password"
          id="password"
          formControlName="password"
          placeholder="••••••••"
          class="w-full p-3 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 outline-none"
          required>
      </div>

      <!-- Forgot password -->
      <div class="flex justify-end mb-5">
        <a href="#" class="text-sm text-blue-600 hover:underline">Forgot password?</a>
      </div>

      <!-- Login Button -->
      <button 
        type="submit"
        class="w-full bg-blue-600 hover:bg-blue-700 text-white text-sm font-medium py-3 rounded-lg transition-all duration-200 focus:ring-4 focus:ring-blue-300 focus:outline-none">
        Login
      </button>

      <!-- Register Link -->
      <p class="mt-5 text-sm text-center text-gray-600">
        Not a member? 
        <a [routerLink]="['/register']" class="text-blue-600 hover:underline">Register</a>
      </p>
    </form>
  </div>
</div>
