import { Component, ElementRef,Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styles: [
  ]
})
export class SearchComponent {
  @Input() search!:ElementRef;
  constructor(private router:Router){}
  onKey(event:KeyboardEvent,element:HTMLInputElement){
    if(event.keyCode===13&&element.value!==''){
    this.router.navigate(['/products'],{ queryParams: { q: element.value.toLowerCase().trim() }});
    }
  }
  onSearch(element:HTMLInputElement){
    this.router.navigate(['/products?q='+element.value]);
  }
}
<div class="flex w-full">
    <span
        class="input-group-text flex items-center whitespace-nowrap rounded bg-white px-3 py-1.5 text-center text-base font-normal text-neutral-700 dark:text-neutral-200"
        id="basic-addon2"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
          class="h-5 w-5"
        >
          <path
            fill-rule="evenodd"
            d="M9 3.5a5.5 5.5 0 100 11 5.5 5.5 0 000-11zM2 9a7 7 0 1112.452 4.391l3.328 3.329a.75.75 0 11-1.06 1.06l-3.329-3.328A7 7 0 012 9z"
            clip-rule="evenodd"
          />
        </svg>
      </span>
      <input
        type="search"
        name="seach"
        placeholder="Search products..."
        aria-label="Search"
        class="relative m-0 block w-[1px] min-w-0 flex-auto focus:z-[3] p-2 md:p-3 outline-none rounded w-full"
        (keydown)="onKey($event,search)"
        #search
      />
</div>
