import { Component, Input,OnInit, ViewChild,ElementRef} from '@angular/core';

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styles: [
  ]
})
export class CarouselComponent implements OnInit{
  @Input() slideImages!:string[];
  @ViewChild('sliderRef') sliderRef!:ElementRef;
  constructor(){
  }
  selectedSlide=0;

  selectSlide(index:number|any){
    this.selectedSlide = index;
  }
  onPrev(){
    let width=this.sliderRef.nativeElement.clientWidth;
    this.sliderRef.nativeElement.scrollLeft=this.sliderRef.nativeElement.scrollLeft-width;
  }
  onNext(){
    let width=this.sliderRef.nativeElement.clientWidth;
    this.sliderRef.nativeElement.scrollLeft=this.sliderRef.nativeElement.scrollLeft+width;
  }
  ngOnInit(): void {
  }

}

<div class="relative w-full">
    <div class="relative h-40 overflow-hidden rounded-lg md:h-96" #sliderRef>
        <div class="duration-700 ease-in-out" *ngFor="let image of slideImages; let index">
            <img [src]="image" class="absolute block w-full -translate-x-1/2 -translate-y-1/2 top-1/2 left-1/2" [alt]="index">
        </div>
    </div>
    <!-- Slider indicators -->
    <div class="absolute z-30 flex space-x-3 -translate-x-1/2 bottom-5 left-1/2">
        <button 
        type="button" 
        class="w-8 h-2  bg-gray-300" 
        *ngFor="let button of slideImages; let index"
        (click)="selectSlide(index)"
        aria-current="true" 
        aria-label="'Slide'+index">
        </button>
    </div>
    <!-- Slider controls -->
    <button 
    type="button" 
    (click)="onPrev()"
    class="absolute top-0 left-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none">
        <span class="inline-flex items-center justify-center w-10 h-10 rounded-full bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
            <svg class="w-4 h-4 text-white dark:text-gray-800" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 1 1 5l4 4"/>
            </svg>
            <span class="sr-only">Previous</span>
        </span>
    </button>
    <button 
    type="button" 
    (click)="onNext()"
    class="absolute top-0 right-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none">
        <span class="inline-flex items-center justify-center w-10 h-10 rounded-full bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
            <svg class="w-4 h-4 text-white dark:text-gray-800" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 6 10">
                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 9 4-4-4-4"/>
            </svg>
            <span class="sr-only">Next</span>
        </span>
    </button>
</div>
